import React , {useState , useEffect}  from 'react';
import {
  MenuItem,
  FormControl,
  Select,
  Card,
  CardContent,
  Table
} from "@material-ui/core" 
import InfoBox from './InfoBox';
import Map from './Map';
import LineGraph from './LineGraph';
import TableA from './TableA';
import './App.css';
import { sortData, prettyPrintStat } from './util';

function App() {
  const [countries,setCountries] = useState([]);
  const [dropdownCountry,setdropdownCountry] = useState('worldwide');
  const [countryInfo, setCountryInfo] = useState({});
  const [tableData, setTableData] = useState([]);
  const[casesType, setCasesType] = useState("cases");

  useEffect(() => {
    fetch("https://disease.sh/v3/covid-19/all")
    .then(response => response.json())
    .then(data => {
      setCountryInfo(data);
    })
  }, [])

  // STATE = thats how we write variables in react
  // USEEFFECT = Runs a piece of code based on a given condition
  // https://disease.sh/v3/covid-19/countries

  useEffect(() => {

    const getCountriesData = async () => {
      await fetch("https://disease.sh/v3/covid-19/countries")
      .then((response) => response.json())
      .then((data) => {
        const countries = data.map((country) => (
          {
            name: country.country,
            value: country.countryInfo.iso2
          }
        ));
        const sortedData = sortData(data);
        setTableData(sortedData);
        setCountries(countries);
      })
    }
      getCountriesData();
  }, [])

  const onCountryChange = async (event) => {
      const countryCode = event.target.value;
      setdropdownCountry(countryCode);

      const url = countryCode === 'worldwide' ? 'https://disease.sh/v3/covid-19/all' : 
      `https://disease.sh/v3/covid-19/countries/${countryCode}`;
      await fetch(url)
      .then(response => response.json())
      .then(data => {
        setdropdownCountry(countryCode);
        setCountryInfo(data);
      })
  }
  console.log(countryInfo)

  return (
    <div className="app"> 

      <div className="app_left">
              {/* Header */}
              {/* Title + Select Input Dropdown */}
              <div className="app_header">
    <h1>COVID-19 TRACKER</h1>
    <FormControl className="app_dropdown">
      <Select variant="outlined" onChange={onCountryChange} value={dropdownCountry}>
      <MenuItem value="worldwide">Worldwide</MenuItem>
        {
          countries.map((country)=> (
            <MenuItem value={country.value}>{country.name}</MenuItem>
          ))
        }

          {/* <MenuItem value="worldwide">Worldwide</MenuItem>
          <MenuItem value="worldwide">India</MenuItem>
          <MenuItem value="worldwide">USA</MenuItem>
          <MenuItem value="worldwide">Australia</MenuItem> */}
        </Select>
    </FormControl>
    </div>

                {/* InfoBoxs */}
                 {/* InfoBoxs */}
                {/* InfoBoxs */}
                 {/* InfoBoxs */}
    <div className="app_stats">
          <InfoBox onClick={e=> setCasesType('cases')} title="Coronavirus Cases" cases={prettyPrintStat(countryInfo.todayCases)} total={prettyPrintStat(countryInfo.cases)}></InfoBox>

          <InfoBox onClick={e=> setCasesType('recovered')} title="Recovered" cases={prettyPrintStat(countryInfo.todayRecovered)} total={prettyPrintStat(countryInfo.recovered)}></InfoBox>

          <InfoBox onClick={e=> setCasesType('deaths')} title="Death" cases={prettyPrintStat(countryInfo.todayDeaths)} total={prettyPrintStat(countryInfo.deaths)}></InfoBox>
    </div>
    {/* Map */}
    <Map></Map>
      </div>
          <div>
            <Card className="app_right">
              <CardContent>
                <h3>Live Cases By Country</h3>
                <TableA countries = { tableData } />
                <h3 className="app__graphTitle">Worldwide New {casesType}</h3>
                {/* Graph */}   
                <LineGraph className="app__graph" casesType={casesType} />

              </CardContent>
            </Card>

          </div>


    </div>
  );
}

export default App;
